import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE_URL = 'http://localhost:8000/api';

function App() {
  const [currentView, setCurrentView] = useState('login');
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));

  useEffect(() => {
    if (token) {
      verifyToken();
    }
  }, [token]);

  const verifyToken = async () => {
    try {
      // –î–ª—è –¥–µ–º–æ-—Ç–æ–∫–µ–Ω–∞ –∏—Å–ø–æ–ª—å–∑—É–µ–º –¥–µ–º–æ-–¥–∞–Ω–Ω—ã–µ
      if (token === 'demo-jwt-token') {
        const demoUser = {
          id: 1,
          badge_id: 'demo',
          full_name: '–ò–≤–∞–Ω–æ–≤ –ò–≤–∞–Ω –ò–≤–∞–Ω–æ–≤–∏—á', // –ò—Å–ø—Ä–∞–≤–ª–µ–Ω–æ –Ω–∞ –ø—Ä–∞–≤–∏–ª—å–Ω–æ–µ –∏–º—è
          is_active: true,
          is_superuser: false
        };
        setUser(demoUser);
        setCurrentView('main');
        return;
      }

      // –†–µ–∞–ª—å–Ω–∞—è –ø—Ä–æ–≤–µ—Ä–∫–∞ —Ç–æ–∫–µ–Ω–∞ —á–µ—Ä–µ–∑ API
      const response = await axios.get(`${API_BASE_URL}/auth/me`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUser(response.data);
      setCurrentView('main');
    } catch (error) {
      console.error('Token verification failed:', error);
      localStorage.removeItem('token');
      setToken(null);
    }
  };

  const handleLogin = (userData, authToken) => {
    setUser(userData);
    setToken(authToken);
    localStorage.setItem('token', authToken);
    setCurrentView('main');
  };

  const handleLogout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('token');
    setCurrentView('login');
  };

  return (
    <div className="App">
      {currentView === 'login' && (
        <LoginView onLogin={handleLogin} />
      )}
      {currentView === 'main' && user && (
        <MainView user={user} onLogout={handleLogout} />
      )}
    </div>
  );
}

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏
function LoginView({ onLogin }) {
  const [formData, setFormData] = useState({
    badge_id: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // –í—Å–µ–≥–¥–∞ –ø—ã—Ç–∞–µ–º—Å—è —Å–Ω–∞—á–∞–ª–∞ –ø–æ–¥–∫–ª—é—á–∏—Ç—å—Å—è –∫ —Ä–µ–∞–ª—å–Ω–æ–º—É API
      const response = await axios.post(`${API_BASE_URL}/auth/login`, formData);
      onLogin(response.data.user, response.data.access_token);
    } catch (error) {
      console.error('Login error:', error);

      // Fallback –Ω–∞ –¥–µ–º–æ-—Ä–µ–∂–∏–º —Ç–æ–ª—å–∫–æ –µ—Å–ª–∏ –±—ç–∫–µ–Ω–¥ –Ω–µ–¥–æ—Å—Ç—É–ø–µ–Ω
      if (error.code === 'NETWORK_ERROR' || error.response?.status >= 500) {
        setError('–ë—ç–∫–µ–Ω–¥ –Ω–µ–¥–æ—Å—Ç—É–ø–µ–Ω. –ò—Å–ø–æ–ª—å–∑—É–µ—Ç—Å—è –¥–µ–º–æ-—Ä–µ–∂–∏–º.');

        await new Promise(resolve => setTimeout(resolve, 1000));

        const demoUser = {
          id: 1,
          badge_id: formData.badge_id || 'demo',
          full_name: formData.badge_id === 'admin' ? '–ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä –°–∏—Å—Ç–µ–º—ã' : '–ò–≤–∞–Ω–æ–≤ –ò–≤–∞–Ω –ò–≤–∞–Ω–æ–≤–∏—á',
          is_active: true,
          is_superuser: formData.badge_id === 'admin'
        };

        onLogin(demoUser, 'demo-jwt-token');
      } else {
        setError('–û—à–∏–±–∫–∞ –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ badge_id –∏ –ø–∞—Ä–æ–ª—å.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleQuickLogin = async (badgeId) => {
    setLoading(true);

    try {
      // –ü—ã—Ç–∞–µ–º—Å—è —Ä–µ–∞–ª—å–Ω—ã–π –ª–æ–≥–∏–Ω
      const response = await axios.post(`${API_BASE_URL}/auth/login`, {
        badge_id: badgeId,
        password: badgeId === 'admin' ? 'admin' : 'password'
      });
      onLogin(response.data.user, response.data.access_token);
    } catch (error) {
      // Fallback –Ω–∞ –¥–µ–º–æ-—Ä–µ–∂–∏–º
      console.log('Using demo mode');
      const demoUser = {
        id: 1,
        badge_id: badgeId,
        full_name: badgeId === 'admin' ? '–ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä –°–∏—Å—Ç–µ–º—ã' : '–ò–≤–∞–Ω–æ–≤ –ò–≤–∞–Ω –ò–≤–∞–Ω–æ–≤–∏—á',
        is_active: true,
        is_superuser: badgeId === 'admin'
      };

      await new Promise(resolve => setTimeout(resolve, 500));
      onLogin(demoUser, 'demo-jwt-token');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h2>–ê–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏—è –æ–ø–µ—Ä–∞—Ç–æ—Ä–∞</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Badge ID:</label>
            <input
              type="text"
              name="badge_id"
              value={formData.badge_id}
              onChange={handleChange}
              required
              disabled={loading}
              placeholder="demo –∏–ª–∏ admin"
            />
          </div>
          <div className="form-group">
            <label>–ü–∞—Ä–æ–ª—å:</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              disabled={loading}
              placeholder="password"
            />
          </div>
          {error && <div className="error-message">{error}</div>}
          <button type="submit" disabled={loading}>
            {loading ? '–í—Ö–æ–¥...' : '–í–æ–π—Ç–∏'}
          </button>
        </form>

        <div className="quick-login-buttons">
          <p style={{textAlign: 'center', margin: '1rem 0', color: '#666'}}>–ë—ã—Å—Ç—Ä—ã–π –≤—Ö–æ–¥:</p>
          <button 
            onClick={() => handleQuickLogin('demo')}
            disabled={loading}
            className="quick-login-btn demo-btn"
          >
            –î–µ–º–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å
          </button>

          <button 
            onClick={() => handleQuickLogin('admin')}
            disabled={loading}
            className="quick-login-btn admin-btn"
          >
            –ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä
          </button>
        </div>

        <div className="demo-info">
          <p><strong>–î–∞–Ω–Ω—ã–µ –¥–ª—è –≤—Ö–æ–¥–∞:</strong></p>
          <p>‚Ä¢ –î–µ–º–æ: badge_id = demo, –ø–∞—Ä–æ–ª—å = password</p>
          <p>‚Ä¢ –ê–¥–º–∏–Ω: badge_id = admin, –ø–∞—Ä–æ–ª—å = admin</p>
        </div>
      </div>
    </div>
  );
}

// –ì–ª–∞–≤–Ω—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç —Ä–∞–±–æ—Ç—ã —Å –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∞–º–∏
function MainView({ user, onLogout }) {
  const [engineer, setEngineer] = useState('');
  const [operationType, setOperationType] = useState('checkout');
  const [operationDate, setOperationDate] = useState('');
  const [tools, setTools] = useState([]);
  const [loading, setLoading] = useState(false);
  const [scanning, setScanning] = useState(false);
  const [sessionId, setSessionId] = useState(null);
  const [uploadedImage, setUploadedImage] = useState(null);

  // –°–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è –∫–∞–º–µ—Ä—ã
  const [cameraActive, setCameraActive] = useState(false);
  const videoRef = useRef(null);
  const [stream, setStream] = useState(null);

  // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º —Ç–µ–∫—É—â—É—é –¥–∞—Ç—É –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é
  useEffect(() => {
    const now = new Date();
    const localDateTime = new Date(now.getTime() - now.getTimezoneOffset() * 60000)
      .toISOString()
      .slice(0, 16);
    setOperationDate(localDateTime);
  }, []);

  // –ê–∫—Ç–∏–≤–∞—Ü–∏—è –∫–∞–º–µ—Ä—ã
  const startCamera = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ 
        video: { width: 1280, height: 720 } 
      });
      setStream(mediaStream);
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
      }
      setCameraActive(true);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –¥–æ—Å—Ç—É–ø–∞ –∫ –∫–∞–º–µ—Ä–µ:', error);
      alert('–ö–∞–º–µ—Ä–∞ –Ω–µ–¥–æ—Å—Ç—É–ø–Ω–∞. –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ –∑–∞–≥—Ä—É–∑–∫—É –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è.');
    }
  };

  // –û—Å—Ç–∞–Ω–æ–≤–∫–∞ –∫–∞–º–µ—Ä—ã
  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
    setCameraActive(false);
  };

  // –ó–∞—Ö–≤–∞—Ç –∫–∞–¥—Ä–∞ –∏ –æ—Ç–ø—Ä–∞–≤–∫–∞ –Ω–∞ –±—ç–∫–µ–Ω–¥
  const captureAndDetect = async () => {
    if (!videoRef.current || !cameraActive) {
      alert('–ö–∞–º–µ—Ä–∞ –Ω–µ –∞–∫—Ç–∏–≤–∏—Ä–æ–≤–∞–Ω–∞');
      return;
    }

    const canvas = document.createElement('canvas');
    canvas.width = videoRef.current.videoWidth;
    canvas.height = videoRef.current.videoHeight;
    const ctx = canvas.getContext('2d');
    ctx.drawImage(videoRef.current, 0, 0);

    const imageBase64 = canvas.toDataURL('image/jpeg').split(',')[1];
    setUploadedImage(imageBase64);

    await performDetection(imageBase64);
  };

  const performDetection = async (imageBase64) => {
    setLoading(true);
    try {
      const response = await axios.post(`${API_BASE_URL}/ml/detect`, {
        image_base64: imageBase64,
        confidence_threshold: 0.7
      });

      if (response.data.success) {
        const detectedTools = response.data.results.detected_tools || [];
        setTools(detectedTools.map((tool, index) => ({
          id: index,
          class_name: tool.class_name,
          confidence: tool.confidence,
          detected_quantity: tool.detected_quantity,
          manual_quantity: tool.detected_quantity || 1,
          is_manual_edit: false
        })));

        alert(`–û–±–Ω–∞—Ä—É–∂–µ–Ω–æ ${detectedTools.length} –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–æ–≤!`);
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –¥–µ—Ç–µ–∫—Ü–∏–∏:', error);
      // –î–µ–º–æ-–¥–∞–Ω–Ω—ã–µ –ø—Ä–∏ –æ—à–∏–±–∫–µ
      const demoTools = getDemoTools();
      setTools(demoTools);
      alert('–ò—Å–ø–æ–ª—å–∑—É—é—Ç—Å—è –¥–µ–º–æ-–¥–∞–Ω–Ω—ã–µ');
    } finally {
      setLoading(false);
    }
  };

  const getDemoTools = () => [
    { id: 1, class_name: "–ú–æ–ª–æ—Ç–æ–∫", confidence: 95.5, detected_quantity: 1, manual_quantity: 1, is_manual_edit: false },
    { id: 2, class_name: "–û—Ç–≤–µ—Ä—Ç–∫–∞ –∫—Ä–µ—Å—Ç–æ–≤–∞—è", confidence: 87.3, detected_quantity: 2, manual_quantity: 2, is_manual_edit: false },
    { id: 3, class_name: "–ì–∞–µ—á–Ω—ã–π –∫–ª—é—á", confidence: 92.1, detected_quantity: 1, manual_quantity: 1, is_manual_edit: false }
  ];

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = async (e) => {
        const base64 = e.target.result.split(',')[1];
        setUploadedImage(base64);
        await performDetection(base64);
      };
      reader.readAsDataURL(file);
    }
  };

  const startScanning = async () => {
    if (!engineer.trim()) {
      alert('–í–≤–µ–¥–∏—Ç–µ –§–ò–û –∏–Ω–∂–µ–Ω–µ—Ä–∞');
      return;
    }

    setScanning(true);
    setLoading(true);

    try {
      // –ü—ã—Ç–∞–µ–º—Å—è —Å–æ–∑–¥–∞—Ç—å –æ–ø–µ—Ä–∞—Ü–∏—é —á–µ—Ä–µ–∑ API
      const operationData = {
        engineer_name: engineer,
        operation_type: operationType,
        operation_date: operationDate,
        user_id: user.id
      };

      const token = localStorage.getItem('token');
      const headers = token !== 'demo-jwt-token' ? { Authorization: `Bearer ${token}` } : {};

      const response = await axios.post(`${API_BASE_URL}/operations/start`, operationData, { headers });
      setSessionId(response.data.session_id);

      // –ü—ã—Ç–∞–µ–º—Å—è –≤–∫–ª—é—á–∏—Ç—å –∫–∞–º–µ—Ä—É
      await startCamera();

    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –Ω–∞—á–∞–ª–∞ –æ–ø–µ—Ä–∞—Ü–∏–∏:', error);
      // –î–µ–º–æ-—Ä–µ–∂–∏–º –ø—Ä–∏ –æ—à–∏–±–∫–µ
      setSessionId('demo-session-' + Date.now());
      await startCamera();
    } finally {
      setScanning(false);
      setLoading(false);
    }
  };

  const updateToolQuantity = (index, newQuantity) => {
    const updatedTools = [...tools];
    updatedTools[index] = {
      ...updatedTools[index],
      manual_quantity: Math.max(0, newQuantity),
      is_manual_edit: true
    };
    setTools(updatedTools);
  };

  const confirmOperation = async () => {
    if (!sessionId) return;

    setLoading(true);
    try {
      let finalImageBase64 = uploadedImage;

      if (!finalImageBase64 && videoRef.current && cameraActive) {
        const canvas = document.createElement('canvas');
        canvas.width = videoRef.current.videoWidth;
        canvas.height = videoRef.current.videoHeight;
        const ctx = canvas.getContext('2d');
        ctx.drawImage(videoRef.current, 0, 0);
        finalImageBase64 = canvas.toDataURL('image/jpeg').split(',')[1];
      }

      const operationData = {
        session_id: sessionId,
        operation_date: operationDate,
        tools: tools.map(tool => ({
          tool_id: tool.id,
          final_quantity: tool.manual_quantity,
          was_manually_adjusted: tool.is_manual_edit
        })),
        image_base64: finalImageBase64
      };

      const token = localStorage.getItem('token');
      const headers = token !== 'demo-jwt-token' ? { Authorization: `Bearer ${token}` } : {};

      const response = await axios.post(`${API_BASE_URL}/operations/confirm`, operationData, { headers });

      stopCamera();

      alert(`–û–ø–µ—Ä–∞—Ü–∏—è –∑–∞–≤–µ—Ä—à–µ–Ω–∞! –û–±—Ä–∞–±–æ—Ç–∞–Ω–æ ${response.data.total_tools} –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–æ–≤`);
      resetOperation();

    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è –æ–ø–µ—Ä–∞—Ü–∏–∏:', error);
      alert('–û–ø–µ—Ä–∞—Ü–∏—è –∑–∞–≤–µ—Ä—à–µ–Ω–∞ –≤ –¥–µ–º–æ-—Ä–µ–∂–∏–º–µ');
      resetOperation();
    } finally {
      setLoading(false);
    }
  };

  const resetOperation = () => {
    setEngineer('');
    setTools([]);
    setSessionId(null);
    setScanning(false);
    setUploadedImage(null);
    stopCamera();

    const now = new Date();
    const localDateTime = new Date(now.getTime() - now.getTimezoneOffset() * 60000)
      .toISOString()
      .slice(0, 16);
    setOperationDate(localDateTime);
  };

  const formatDateTime = (dateTimeString) => {
    return new Date(dateTimeString).toLocaleString('ru-RU');
  };

  return (
    <div className="main-container">
      <header className="header">
        <h1>–£—á–µ—Ç –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–æ–≤</h1>
        <div className="user-info">
          <span>–û–ø–µ—Ä–∞—Ç–æ—Ä: {user.full_name}</span>
          {user.is_superuser && <span className="admin-badge">ADMIN</span>}
          <button onClick={onLogout}>–í—ã–π—Ç–∏</button>
        </div>
      </header>

      <div className="operation-panel">
        <div className="input-section">
          <div className="form-group">
            <label>–§–ò–û –∏–Ω–∂–µ–Ω–µ—Ä–∞:</label>
            <input
              type="text"
              value={engineer}
              onChange={(e) => setEngineer(e.target.value)}
              placeholder="–í–≤–µ–¥–∏—Ç–µ —Ñ–∞–º–∏–ª–∏—é, –∏–º—è, –æ—Ç—á–µ—Å—Ç–≤–æ"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label>–¢–∏–ø –æ–ø–µ—Ä–∞—Ü–∏–∏:</label>
            <select
              value={operationType}
              onChange={(e) => setOperationType(e.target.value)}
              disabled={loading}
            >
              <option value="checkout">–í—ã–¥–∞—á–∞ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–æ–≤</option>
              <option value="checkin">–ü—Ä–∏–µ–º –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–æ–≤</option>
            </select>
          </div>

          <div className="form-group">
            <label>–î–∞—Ç–∞ –∏ –≤—Ä–µ–º—è –æ–ø–µ—Ä–∞—Ü–∏–∏:</label>
            <input
              type="datetime-local"
              value={operationDate}
              onChange={(e) => setOperationDate(e.target.value)}
              disabled={loading}
            />
          </div>

          <div className="action-buttons">
            <button 
              onClick={startScanning}
              disabled={!engineer.trim() || loading}
              className="scan-button"
            >
              {loading ? '–ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞...' : '–ù–∞—á–∞—Ç—å –æ–ø–µ—Ä–∞—Ü–∏—é'}
            </button>

            {tools.length > 0 && (
              <button 
                onClick={confirmOperation}
                disabled={loading}
                className="confirm-button"
              >
                {loading ? '–°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ...' : '–ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç—å –æ–ø–µ—Ä–∞—Ü–∏—é'}
              </button>
            )}
          </div>
        </div>

        {/* –°–µ–∫—Ü–∏—è —Ä–∞–±–æ—Ç—ã —Å –∫–∞–º–µ—Ä–æ–π */}
        <div className="camera-section">
          <h3>–†–∞–±–æ—Ç–∞ —Å –∫–∞–º–µ—Ä–æ–π</h3>

          <div className="camera-controls">
            {!cameraActive ? (
              <button 
                onClick={startCamera} 
                disabled={!sessionId || loading}
                className="camera-button"
              >
                üì∑ –í–∫–ª—é—á–∏—Ç—å –∫–∞–º–µ—Ä—É
              </button>
            ) : (
              <>
                <button onClick={stopCamera} className="camera-button stop">
                  ‚èπÔ∏è –í—ã–∫–ª—é—á–∏—Ç—å –∫–∞–º–µ—Ä—É
                </button>
                <button 
                  onClick={captureAndDetect} 
                  disabled={loading} 
                  className="camera-button capture"
                >
                  {loading ? 'üîç –†–∞—Å–ø–æ–∑–Ω–∞–≤–∞–Ω–∏–µ...' : 'üì∏ –†–∞—Å–ø–æ–∑–Ω–∞—Ç—å –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç—ã'}
                </button>

                <div className="file-upload">
                  <label htmlFor="image-upload" className="upload-button">
                    üìÅ –ó–∞–≥—Ä—É–∑–∏—Ç—å –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ
                  </label>
                  <input
                    id="image-upload"
                    type="file"
                    accept="image/*"
                    onChange={handleImageUpload}
                    style={{ display: 'none' }}
                  />
                </div>
              </>
            )}
          </div>

          {cameraActive && (
            <div className="video-container">
              <video 
                ref={videoRef}
                autoPlay 
                playsInline
                muted
                className="camera-video"
              />
              <div className="camera-overlay">
                <div className="scan-frame"></div>
                <p>–ù–∞–≤–µ–¥–∏—Ç–µ –∫–∞–º–µ—Ä—É –Ω–∞ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç—ã</p>
              </div>
            </div>
          )}

          {uploadedImage && !cameraActive && (
            <div className="uploaded-image-preview">
              <p>üì∑ –ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ –∑–∞–≥—Ä—É–∂–µ–Ω–æ</p>
              <button 
                onClick={() => performDetection(uploadedImage)} 
                disabled={loading} 
                className="camera-button capture"
              >
                {loading ? 'üîç –†–∞—Å–ø–æ–∑–Ω–∞–≤–∞–Ω–∏–µ...' : 'üì∏ –†–∞—Å–ø–æ–∑–Ω–∞—Ç—å –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç—ã'}
              </button>
            </div>
          )}
        </div>

        {tools.length > 0 && (
          <div className="tools-section">
            <h3>–†–∞—Å–ø–æ–∑–Ω–∞–Ω–Ω—ã–µ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç—ã:</h3>
            <div className="tools-list">
              {tools.map((tool, index) => (
                <ToolItem
                  key={index}
                  tool={tool}
                  index={index}
                  onQuantityChange={updateToolQuantity}
                  operationType={operationType}
                />
              ))}
            </div>

            <div className="operation-summary">
              <p><strong>–ò—Ç–æ–≥–æ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–æ–≤:</strong> {tools.reduce((sum, tool) => sum + tool.manual_quantity, 0)}</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∞
function ToolItem({ tool, index, onQuantityChange, operationType }) {
  const [localQuantity, setLocalQuantity] = useState(tool.manual_quantity);

  const handleQuantityChange = (newQuantity) => {
    setLocalQuantity(newQuantity);
    onQuantityChange(index, newQuantity);
  };

  return (
    <div className={`tool-item ${tool.is_manual_edit ? 'manual-edit' : ''}`}>
      <div className="tool-info">
        <span className="tool-name">{tool.class_name}</span>
        {tool.confidence && (
          <span className="confidence">{tool.confidence.toFixed(1)}%</span>
        )}
      </div>

      <div className="quantity-controls">
        <button onClick={() => handleQuantityChange(localQuantity - 1)}>-</button>
        <input
          type="number"
          value={localQuantity}
          onChange={(e) => handleQuantityChange(parseInt(e.target.value) || 0)}
          min="0"
        />
        <button onClick={() => handleQuantityChange(localQuantity + 1)}>+</button>
      </div>
    </div>
  );
}

export default App;
