import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE_URL = 'http://localhost:8000/api';

function App() {
  const [currentView, setCurrentView] = useState('login');
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));

  useEffect(() => {
    if (token) {
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º –≤–∞–ª–∏–¥–Ω–æ—Å—Ç—å —Ç–æ–∫–µ–Ω–∞
      verifyToken();
    }
  }, [token]);

  const verifyToken = async () => {
    try {
      // –í –¥–µ–º–æ-—Ä–µ–∂–∏–º–µ –ø—Ä–æ–ø—É—Å–∫–∞–µ–º —Ä–µ–∞–ª—å–Ω—É—é –ø—Ä–æ–≤–µ—Ä–∫—É
      if (token === 'demo-jwt-token') {
        const demoUser = {
          id: 1,
          badge_id: 'demo',
          full_name: '–î–µ–º–æ –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å',
          is_active: true,
          is_superuser: false
        };
        setUser(demoUser);
        setCurrentView('main');
        return;
      }

      const response = await axios.get(`${API_BASE_URL}/auth/me`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUser(response.data);
      setCurrentView('main');
    } catch (error) {
      localStorage.removeItem('token');
      setToken(null);
    }
  };

  const handleLogin = (userData, authToken) => {
    setUser(userData);
    setToken(authToken);
    localStorage.setItem('token', authToken);
    setCurrentView('main');
  };

  const handleLogout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('token');
    setCurrentView('login');
  };

  return (
    <div className="App">
      {currentView === 'login' && (
        <LoginView onLogin={handleLogin} />
      )}
      {currentView === 'main' && user && (
        <MainView user={user} onLogout={handleLogout} />
      )}
    </div>
  );
}

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏
function LoginView({ onLogin }) {
  const [formData, setFormData] = useState({
    badge_id: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    if (e && e.preventDefault) {
      e.preventDefault();
    }

    setLoading(true);
    setError('');

    // –î–µ–º–æ-—Ä–µ–∂–∏–º - –ø—Ä–æ–ø—É—Å–∫–∞–µ–º —Ä–µ–∞–ª—å–Ω—ã–π –∑–∞–ø—Ä–æ—Å –∫ API
    if (formData.badge_id === 'demo' || formData.badge_id === 'admin') {
      await new Promise(resolve => setTimeout(resolve, 1000));

      const demoUser = {
        id: 1,
        badge_id: formData.badge_id,
        full_name: formData.badge_id === 'admin' ? '–ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä –°–∏—Å—Ç–µ–º—ã' : '–ò–≤–∞–Ω–æ–≤ –ò–≤–∞–Ω –ò–≤–∞–Ω–æ–≤–∏—á',
        is_active: true,
        is_superuser: formData.badge_id === 'admin'
      };

      onLogin(demoUser, 'demo-jwt-token');
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post(`${API_BASE_URL}/auth/login`, formData);
      onLogin(response.data.user, response.data.access_token);
    } catch (error) {
      setError('–û—à–∏–±–∫–∞ –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ badge_id –∏ –ø–∞—Ä–æ–ª—å.');

      await new Promise(resolve => setTimeout(resolve, 1000));
      const demoUser = {
        id: 1,
        badge_id: 'demo',
        full_name: '–î–µ–º–æ –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å',
        is_active: true,
        is_superuser: false
      };
      onLogin(demoUser, 'demo-jwt-token');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleQuickLogin = async (badgeId) => {
    setLoading(true);

    setFormData({ 
      badge_id: badgeId, 
      password: 'password' 
    });

    await new Promise(resolve => setTimeout(resolve, 100));

    const demoUser = {
      id: 1,
      badge_id: badgeId,
      full_name: badgeId === 'admin' ? '–ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä –°–∏—Å—Ç–µ–º—ã' : '–ò–≤–∞–Ω–æ–≤ –ò–≤–∞–Ω –ò–≤–∞–Ω–æ–≤–∏—á',
      is_active: true,
      is_superuser: badgeId === 'admin'
    };

    await new Promise(resolve => setTimeout(resolve, 1000));

    onLogin(demoUser, 'demo-jwt-token');
    setLoading(false);
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h2>–ê–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏—è –æ–ø–µ—Ä–∞—Ç–æ—Ä–∞</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Badge ID:</label>
            <input
              type="text"
              name="badge_id"
              value={formData.badge_id}
              onChange={handleChange}
              required
              disabled={loading}
            />
          </div>
          <div className="form-group">
            <label>–ü–∞—Ä–æ–ª—å:</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              disabled={loading}
            />
          </div>
          {error && <div className="error-message">{error}</div>}
          <button type="submit" disabled={loading}>
            {loading ? '–í—Ö–æ–¥...' : '–í–æ–π—Ç–∏'}
          </button>
        </form>

        <div className="quick-login-buttons">
          <p style={{textAlign: 'center', margin: '1rem 0', color: '#666'}}>–ò–ª–∏ –≤–æ–π–¥–∏—Ç–µ –±—ã—Å—Ç—Ä–æ:</p>
          <button 
            onClick={() => handleQuickLogin('demo')}
            disabled={loading}
            className="quick-login-btn demo-btn"
          >
            {loading ? '–í—Ö–æ–¥...' : '–ë—ã—Å—Ç—Ä—ã–π –≤—Ö–æ–¥ (Demo –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å)'}
          </button>

          <button 
            onClick={() => handleQuickLogin('admin')}
            disabled={loading}
            className="quick-login-btn admin-btn"
          >
            {loading ? '–í—Ö–æ–¥...' : '–í—Ö–æ–¥ –∫–∞–∫ –ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä'}
          </button>
        </div>

        <div className="demo-info">
          <p>
            <strong>–î–µ–º–æ-—Ä–µ–∂–∏–º:</strong> –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ –∫–Ω–æ–ø–∫–∏ –±—ã—Å—Ç—Ä–æ–≥–æ –≤—Ö–æ–¥–∞ –∏–ª–∏ –≤–≤–µ–¥–∏—Ç–µ –ª—é–±—ã–µ –¥–∞–Ω–Ω—ã–µ –≤ —Ñ–æ—Ä–º—É
          </p>
        </div>
      </div>
    </div>
  );
}

// –ì–ª–∞–≤–Ω—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç —Ä–∞–±–æ—Ç—ã —Å –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∞–º–∏
function MainView({ user, onLogout }) {
  const [engineer, setEngineer] = useState('');
  const [operationType, setOperationType] = useState('checkout');
  const [operationDate, setOperationDate] = useState('');
  const [tools, setTools] = useState([]);
  const [loading, setLoading] = useState(false);
  const [scanning, setScanning] = useState(false);
  const [sessionId, setSessionId] = useState(null);
  const [uploadedImage, setUploadedImage] = useState(null);

  // –°–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è –∫–∞–º–µ—Ä—ã
  const [cameraActive, setCameraActive] = useState(false);
  const [videoRef, setVideoRef] = useState(null);
  const [stream, setStream] = useState(null);

  // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º —Ç–µ–∫—É—â—É—é –¥–∞—Ç—É –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ
  useEffect(() => {
    const now = new Date();
    const localDateTime = new Date(now.getTime() - now.getTimezoneOffset() * 60000)
      .toISOString()
      .slice(0, 16);
    setOperationDate(localDateTime);
  }, []);

  // –ê–∫—Ç–∏–≤–∞—Ü–∏—è –∫–∞–º–µ—Ä—ã
  const startCamera = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ 
        video: { width: 1280, height: 720 } 
      });
      setStream(mediaStream);
      if (videoRef) {
        videoRef.srcObject = mediaStream;
      }
      setCameraActive(true);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –¥–æ—Å—Ç—É–ø–∞ –∫ –∫–∞–º–µ—Ä–µ:', error);
      alert('–ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å –¥–æ—Å—Ç—É–ø –∫ –∫–∞–º–µ—Ä–µ');
    }
  };

  // –û—Å—Ç–∞–Ω–æ–≤–∫–∞ –∫–∞–º–µ—Ä—ã
  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
    setCameraActive(false);
  };

  // –ó–∞—Ö–≤–∞—Ç –∫–∞–¥—Ä–∞ –∏ –æ—Ç–ø—Ä–∞–≤–∫–∞ –Ω–∞ –±—ç–∫–µ–Ω–¥ –¥–ª—è —Ä–∞—Å–ø–æ–∑–Ω–∞–≤–∞–Ω–∏—è
  const captureAndDetect = async () => {
    if (!videoRef || !cameraActive) return;

    const canvas = document.createElement('canvas');
    canvas.width = videoRef.videoWidth;
    canvas.height = videoRef.videoHeight;
    const ctx = canvas.getContext('2d');

    // –†–∏—Å—É–µ–º —Ç–µ–∫—É—â–∏–π –∫–∞–¥—Ä —Å –≤–∏–¥–µ–æ
    ctx.drawImage(videoRef, 0, 0);

    // –ö–æ–Ω–≤–µ—Ä—Ç–∏—Ä—É–µ–º –≤ base64
    const imageBase64 = canvas.toDataURL('image/jpeg').split(',')[1];
    setUploadedImage(imageBase64); // –°–æ—Ö—Ä–∞–Ω—è–µ–º –¥–ª—è –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è –æ–ø–µ—Ä–∞—Ü–∏–∏

    try {
      setLoading(true);
      const response = await axios.post(`${API_BASE_URL}/ml/detect`, {
        image_base64: imageBase64,
        confidence_threshold: 0.7
      });

      if (response.data.success) {
        const detectedTools = response.data.results.detected_tools || [];
        setTools(detectedTools.map((tool, index) => ({
          id: index,
          class_name: tool.class_name,
          confidence: tool.confidence,
          detected_quantity: tool.detected_quantity,
          manual_quantity: tool.detected_quantity || 0,
          is_manual_edit: false
        })));

        alert(`–û–±–Ω–∞—Ä—É–∂–µ–Ω–æ ${detectedTools.length} –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–æ–≤!`);
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –¥–µ—Ç–µ–∫—Ü–∏–∏:', error);
      // Fallback –∫ –¥–µ–º–æ-–¥–∞–Ω–Ω—ã–º –ø—Ä–∏ –æ—à–∏–±–∫–µ
      const demoTools = [
        { id: 1, class_name: "–ú–æ–ª–æ—Ç–æ–∫", confidence: 95.5, detected_quantity: 1 },
        { id: 2, class_name: "–û—Ç–≤–µ—Ä—Ç–∫–∞ –∫—Ä–µ—Å—Ç–æ–≤–∞—è", confidence: 87.3, detected_quantity: 2 },
        { id: 3, class_name: "–ì–∞–µ—á–Ω—ã–π –∫–ª—é—á", confidence: 92.1, detected_quantity: 3 },
        { id: 4, class_name: "–ü–ª–æ—Å–∫–æ–≥—É–±—Ü—ã", confidence: 78.9, detected_quantity: 1 }
      ];

      setTools(demoTools.map(tool => ({
        ...tool,
        manual_quantity: tool.detected_quantity || 0,
        is_manual_edit: false
      })));

      alert('–ò—Å–ø–æ–ª—å–∑—É—é—Ç—Å—è –¥–µ–º–æ-–¥–∞–Ω–Ω—ã–µ (ML —Å–µ—Ä–≤–∏—Å –Ω–µ–¥–æ—Å—Ç—É–ø–µ–Ω)');
    } finally {
      setLoading(false);
    }
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const base64 = e.target.result.split(',')[1];
        setUploadedImage(base64);
      };
      reader.readAsDataURL(file);
    }
  };

  const startScanning = async () => {
    if (!engineer.trim()) {
      alert('–í–≤–µ–¥–∏—Ç–µ –§–ò–û –∏–Ω–∂–µ–Ω–µ—Ä–∞');
      return;
    }

    if (!operationDate) {
      alert('–í—ã–±–µ—Ä–∏—Ç–µ –¥–∞—Ç—É –∏ –≤—Ä–µ–º—è –æ–ø–µ—Ä–∞—Ü–∏–∏');
      return;
    }

    setScanning(true);
    setLoading(true);

    try {
      // –ù–∞—á–∏–Ω–∞–µ–º –Ω–æ–≤—É—é –æ–ø–µ—Ä–∞—Ü–∏—é
      const operationData = {
        engineer_name: engineer,
        operation_type: operationType,
        operation_date: operationDate,
        user_id: user.id
      };

      const response = await axios.post(`${API_BASE_URL}/operations/start`, operationData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      setSessionId(response.data.session_id);

      // –í–∫–ª—é—á–∞–µ–º –∫–∞–º–µ—Ä—É –¥–ª—è —Å–∫–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏—è
      await startCamera();

    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –Ω–∞—á–∞–ª–∞ –æ–ø–µ—Ä–∞—Ü–∏–∏:', error);
      // Fallback –¥–ª—è –¥–µ–º–æ-—Ä–µ–∂–∏–º–∞
      setSessionId('demo-session-' + Date.now());
      await startCamera();
    } finally {
      setScanning(false);
      setLoading(false);
    }
  };

  const updateToolQuantity = (index, newQuantity) => {
    const updatedTools = [...tools];
    updatedTools[index] = {
      ...updatedTools[index],
      manual_quantity: Math.max(0, newQuantity),
      is_manual_edit: true
    };
    setTools(updatedTools);
  };

  const confirmOperation = async () => {
    if (!sessionId) return;

    setLoading(true);
    try {
      let finalImageBase64 = uploadedImage;

      // –ï—Å–ª–∏ –Ω–µ—Ç –∑–∞–≥—Ä—É–∂–µ–Ω–Ω–æ–≥–æ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è, –Ω–æ –∫–∞–º–µ—Ä–∞ –∞–∫—Ç–∏–≤–Ω–∞ - –∑–∞—Ö–≤–∞—Ç—ã–≤–∞–µ–º –∫–∞–¥—Ä
      if (!finalImageBase64 && videoRef && cameraActive) {
        const canvas = document.createElement('canvas');
        canvas.width = videoRef.videoWidth;
        canvas.height = videoRef.videoHeight;
        const ctx = canvas.getContext('2d');
        ctx.drawImage(videoRef, 0, 0);
        finalImageBase64 = canvas.toDataURL('image/jpeg').split(',')[1];
      }

      const operationData = {
        session_id: sessionId,
        operation_date: operationDate,
        tools: tools.map(tool => ({
          tool_id: tool.id,
          final_quantity: tool.manual_quantity,
          was_manually_adjusted: tool.is_manual_edit
        })),
        image_base64: finalImageBase64
      };

      const response = await axios.post(`${API_BASE_URL}/operations/confirm`, operationData);

      // –í—ã–∫–ª—é—á–∞–µ–º –∫–∞–º–µ—Ä—É –ø–æ—Å–ª–µ –æ–ø–µ—Ä–∞—Ü–∏–∏
      stopCamera();

      // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã ML –µ—Å–ª–∏ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–ª–∏—Å—å
      if (response.data.ml_used) {
        alert(`ML –æ–±–Ω–∞—Ä—É–∂–µ–Ω–æ ${response.data.total_tools} –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–æ–≤! –°–æ–≤–ø–∞–¥–µ–Ω–∏–µ: ${response.data.match_percentage}%`);
      } else {
        alert(`–û–ø–µ—Ä–∞—Ü–∏—è —É—Å–ø–µ—à–Ω–æ –∑–∞–≤–µ—Ä—à–µ–Ω–∞! –û–±—Ä–∞–±–æ—Ç–∞–Ω–æ ${response.data.total_tools} –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–æ–≤`);
      }

      resetOperation();

    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è –æ–ø–µ—Ä–∞—Ü–∏–∏:', error);
      alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≤–µ—Ä—à–µ–Ω–∏–∏ –æ–ø–µ—Ä–∞—Ü–∏–∏');
    } finally {
      setLoading(false);
    }
  };

  const resetOperation = () => {
    setEngineer('');
    setTools([]);
    setSessionId(null);
    setScanning(false);
    setUploadedImage(null);
    stopCamera();

    // –°–±—Ä–∞—Å—ã–≤–∞–µ–º –¥–∞—Ç—É –Ω–∞ —Ç–µ–∫—É—â—É—é
    const now = new Date();
    const localDateTime = new Date(now.getTime() - now.getTimezoneOffset() * 60000)
      .toISOString()
      .slice(0, 16);
    setOperationDate(localDateTime);
  };

  const formatDateTime = (dateTimeString) => {
    const date = new Date(dateTimeString);
    return date.toLocaleString('ru-RU', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="main-container">
      <header className="header">
        <h1>–£—á–µ—Ç –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–æ–≤</h1>
        <div className="user-info">
          <span>–û–ø–µ—Ä–∞—Ç–æ—Ä: {user.full_name}</span>
          {user.is_superuser && <span className="admin-badge">ADMIN</span>}
          <button onClick={onLogout}>–í—ã–π—Ç–∏</button>
        </div>
      </header>

      <div className="operation-panel">
        <div className="input-section">
          <div className="form-group">
            <label>–§–ò–û –∏–Ω–∂–µ–Ω–µ—Ä–∞:</label>
            <input
              type="text"
              value={engineer}
              onChange={(e) => setEngineer(e.target.value)}
              placeholder="–í–≤–µ–¥–∏—Ç–µ —Ñ–∞–º–∏–ª–∏—é, –∏–º—è, –æ—Ç—á–µ—Å—Ç–≤–æ"
              disabled={scanning || loading || cameraActive}
            />
          </div>

          <div className="form-group">
            <label>–¢–∏–ø –æ–ø–µ—Ä–∞—Ü–∏–∏:</label>
            <select
              value={operationType}
              onChange={(e) => setOperationType(e.target.value)}
              disabled={scanning || loading || cameraActive}
            >
              <option value="checkout">–í—ã–¥–∞–ª –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç—ã</option>
              <option value="checkin">–ü—Ä–∏–Ω—è–ª –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç—ã</option>
            </select>
          </div>

          <div className="form-group">
            <label>–î–∞—Ç–∞ –∏ –≤—Ä–µ–º—è –æ–ø–µ—Ä–∞—Ü–∏–∏:</label>
            <input
              type="datetime-local"
              value={operationDate}
              onChange={(e) => setOperationDate(e.target.value)}
              disabled={scanning || loading || cameraActive}
            />
            <small className="date-hint">
              –¢–µ–∫—É—â–µ–µ –≤—Ä–µ–º—è: {formatDateTime(new Date())}
            </small>
          </div>

          <div className="action-buttons">
            <button 
              onClick={startScanning}
              disabled={!engineer.trim() || !operationDate || scanning || loading || cameraActive}
              className="scan-button"
            >
              {scanning ? '–ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞...' : '–ù–∞—á–∞—Ç—å –æ–ø–µ—Ä–∞—Ü–∏—é'}
            </button>

            {tools.length > 0 && (
              <button 
                onClick={confirmOperation}
                disabled={loading}
                className="confirm-button"
              >
                {loading ? '–°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ...' : '–ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç—å –æ–ø–µ—Ä–∞—Ü–∏—é'}
              </button>
            )}
          </div>
        </div>

        {/* –°–µ–∫—Ü–∏—è —Ä–∞–±–æ—Ç—ã —Å –∫–∞–º–µ—Ä–æ–π */}
        <div className="camera-section">
          <h3>–†–∞–±–æ—Ç–∞ —Å –∫–∞–º–µ—Ä–æ–π</h3>

          <div className="camera-controls">
            {!cameraActive ? (
              <button 
                onClick={startCamera} 
                disabled={!sessionId}
                className="camera-button"
              >
                üì∑ –í–∫–ª—é—á–∏—Ç—å –∫–∞–º–µ—Ä—É
              </button>
            ) : (
              <>
                <button onClick={stopCamera} className="camera-button stop">
                  ‚èπÔ∏è –í—ã–∫–ª—é—á–∏—Ç—å –∫–∞–º–µ—Ä—É
                </button>
                <button 
                  onClick={captureAndDetect} 
                  disabled={loading} 
                  className="camera-button capture"
                >
                  {loading ? 'üîç –†–∞—Å–ø–æ–∑–Ω–∞–≤–∞–Ω–∏–µ...' : 'üì∏ –†–∞—Å–ø–æ–∑–Ω–∞—Ç—å –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç—ã'}
                </button>

                <div className="file-upload">
                  <label htmlFor="image-upload" className="upload-button">
                    üìÅ –ó–∞–≥—Ä—É–∑–∏—Ç—å –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ
                  </label>
                  <input
                    id="image-upload"
                    type="file"
                    accept="image/*"
                    onChange={handleImageUpload}
                    style={{ display: 'none' }}
                  />
                </div>
              </>
            )}
          </div>

          {cameraActive && (
            <div className="video-container">
              <video 
                ref={setVideoRef}
                autoPlay 
                playsInline
                muted
                className="camera-video"
              />
              <div className="camera-overlay">
                <div className="scan-frame"></div>
                <p>–ù–∞–≤–µ–¥–∏—Ç–µ –∫–∞–º–µ—Ä—É –Ω–∞ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç—ã</p>
              </div>
            </div>
          )}

          {uploadedImage && !cameraActive && (
            <div className="uploaded-image-preview">
              <p>üì∑ –ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ –∑–∞–≥—Ä—É–∂–µ–Ω–æ –∏ –≥–æ—Ç–æ–≤–æ –∫ —Ä–∞—Å–ø–æ–∑–Ω–∞–≤–∞–Ω–∏—é</p>
              <button 
                onClick={captureAndDetect} 
                disabled={loading} 
                className="camera-button capture"
              >
                {loading ? 'üîç –†–∞—Å–ø–æ–∑–Ω–∞–≤–∞–Ω–∏–µ...' : 'üì∏ –†–∞—Å–ø–æ–∑–Ω–∞—Ç—å –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç—ã'}
              </button>
            </div>
          )}
        </div>

        {scanning && (
          <div className="scanning-overlay">
            <div className="scanning-animation">
              <div className="scanner"></div>
              <p>–ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –æ–ø–µ—Ä–∞—Ü–∏–∏...</p>
            </div>
          </div>
        )}

        {tools.length > 0 && (
          <div className="tools-section">
            <div className="operation-info">
              <h3>–î–µ—Ç–∞–ª–∏ –æ–ø–µ—Ä–∞—Ü–∏–∏:</h3>
              <p><strong>–ò–Ω–∂–µ–Ω–µ—Ä:</strong> {engineer}</p>
              <p><strong>–¢–∏–ø –æ–ø–µ—Ä–∞—Ü–∏–∏:</strong> {operationType === 'checkout' ? '–í—ã–¥–∞—á–∞' : '–ü—Ä–∏–µ–º'}</p>
              <p><strong>–î–∞—Ç–∞ –∏ –≤—Ä–µ–º—è:</strong> {formatDateTime(operationDate)}</p>
              <p><strong>–°–µ—Å—Å–∏—è:</strong> {sessionId}</p>
            </div>

            <h3>–†–∞—Å–ø–æ–∑–Ω–∞–Ω–Ω—ã–µ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç—ã:</h3>
            <div className="tools-list">
              {tools.map((tool, index) => (
                <ToolItem
                  key={index}
                  tool={tool}
                  index={index}
                  onQuantityChange={updateToolQuantity}
                  operationType={operationType}
                />
              ))}
            </div>

            <div className="operation-summary">
              <p><strong>–ò—Ç–æ–≥–æ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–æ–≤:</strong> {tools.reduce((sum, tool) => sum + tool.manual_quantity, 0)}</p>
              <p><strong>–û–±–Ω–∞—Ä—É–∂–µ–Ω–æ ML:</strong> {tools.reduce((sum, tool) => sum + (tool.detected_quantity || 0), 0)}</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –æ—Ç–¥–µ–ª—å–Ω–æ–≥–æ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∞
function ToolItem({ tool, index, onQuantityChange, operationType }) {
  const [localQuantity, setLocalQuantity] = useState(tool.manual_quantity);

  const handleQuantityChange = (newQuantity) => {
    setLocalQuantity(newQuantity);
    onQuantityChange(index, newQuantity);
  };

  return (
    <div className={`tool-item ${tool.is_manual_edit ? 'manual-edit' : ''}`}>
      <div className="tool-info">
        <span className="tool-name">{tool.class_name || tool.name}</span>
        {tool.confidence && (
          <span className="confidence">–¢–æ—á–Ω–æ—Å—Ç—å: {tool.confidence.toFixed(1)}%</span>
        )}
      </div>

      <div className="quantity-controls">
        <button 
          onClick={() => handleQuantityChange(localQuantity - 1)}
          disabled={localQuantity <= 0}
        >
          -
        </button>

        <input
          type="number"
          value={localQuantity}
          onChange={(e) => handleQuantityChange(parseInt(e.target.value) || 0)}
          min="0"
        />

        <button onClick={() => handleQuantityChange(localQuantity + 1)}>
          +
        </button>
      </div>

      <div className="operation-indicator">
        {operationType === 'checkout' ? 'üì§ –í—ã–¥–∞—á–∞' : 'üì• –ü—Ä–∏–µ–º'}
      </div>
    </div>
  );
}

export default App; // ‚úÖ –í–∞–∂–Ω–æ: —ç–∫—Å–ø–æ—Ä—Ç –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞ App
