import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE_URL = 'http://localhost:8000/api';

function App() {
  const [currentView, setCurrentView] = useState('login');
  const [user, setUser] = useState(null);
  const [backendStatus, setBackendStatus] = useState('checking');

  // Проверяем доступность бэкенда при загрузке
  useEffect(() => {
    checkBackendStatus();
  }, []);

  const checkBackendStatus = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/health`, { timeout: 5000 });
      setBackendStatus('available');
      console.log('✅ Бэкенд доступен');
    } catch (error) {
      setBackendStatus('unavailable');
      console.log('❌ Бэкенд недоступен, используем демо-режим');
    }
  };

  const handleLogin = (userData) => {
    setUser(userData);
    setCurrentView('main');
  };

  const handleLogout = () => {
    setUser(null);
    setCurrentView('login');
  };

  return (
    <div className="App">
      {backendStatus === 'checking' && (
        <div className="loading-container">
          <h2>🔍 Проверка подключения к серверу...</h2>
        </div>
      )}

      {backendStatus === 'unavailable' && (
        <div className="warning-container">
          <h2>⚠️ Сервер недоступен</h2>
          <p>Используется демо-режим. Запустите бэкенд для полной функциональности.</p>
          <button onClick={() => setBackendStatus('available')}>Продолжить в демо-режиме</button>
        </div>
      )}

      {backendStatus === 'available' && currentView === 'login' && (
        <LoginView onLogin={handleLogin} backendStatus={backendStatus} />
      )}

      {backendStatus === 'available' && currentView === 'main' && user && (
        <MainView user={user} onLogout={handleLogout} backendStatus={backendStatus} />
      )}
    </div>
  );
}

// Упрощенный компонент авторизации
function LoginView({ onLogin, backendStatus }) {
  const [formData, setFormData] = useState({
    badge_id: 'demo',
    password: 'password'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // Пытаемся реальную авторизацию
      const response = await axios.post(`${API_BASE_URL}/auth/login`, formData, { timeout: 10000 });
      onLogin(response.data.user);
    } catch (error) {
      console.error('Ошибка авторизации:', error);

      // Автоматический fallback на демо-режим
      const demoUser = {
        id: 1,
        badge_id: formData.badge_id,
        full_name: formData.badge_id === 'admin' ? 'Администратор Системы' : 'Иванов Иван Иванович',
        is_active: true,
        is_superuser: formData.badge_id === 'admin'
      };

      onLogin(demoUser);
    } finally {
      setLoading(false);
    }
  };

  const handleQuickLogin = (badgeId) => {
    const demoUser = {
      id: 1,
      badge_id: badgeId,
      full_name: badgeId === 'admin' ? 'Администратор Системы' : 'Иванов Иван Иванович',
      is_active: true,
      is_superuser: badgeId === 'admin'
    };

    onLogin(demoUser);
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h2>Авторизация оператора</h2>
        <div className="backend-status">
          {backendStatus === 'available' ? '✅ Сервер доступен' : '⚠️ Демо-режим'}
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Badge ID:</label>
            <input
              type="text"
              name="badge_id"
              value={formData.badge_id}
              onChange={(e) => setFormData({...formData, badge_id: e.target.value})}
              required
              disabled={loading}
            />
          </div>
          <div className="form-group">
            <label>Пароль:</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={(e) => setFormData({...formData, password: e.target.value})}
              required
              disabled={loading}
            />
          </div>
          {error && <div className="error-message">{error}</div>}
          <button type="submit" disabled={loading}>
            {loading ? 'Вход...' : 'Войти'}
          </button>
        </form>

        <div className="quick-login-buttons">
          <button onClick={() => handleQuickLogin('demo')} className="quick-login-btn">
            Демо пользователь
          </button>
          <button onClick={() => handleQuickLogin('admin')} className="quick-login-btn admin-btn">
            Администратор
          </button>
        </div>
      </div>
    </div>
  );
}

// Упрощенный главный компонент
function MainView({ user, onLogout, backendStatus }) {
  const [engineer, setEngineer] = useState('');
  const [tools, setTools] = useState([]);
  const [loading, setLoading] = useState(false);

  const startOperation = async () => {
    if (!engineer.trim()) {
      alert('Введите ФИО инженера');
      return;
    }

    setLoading(true);

    try {
      // Пытаемся создать операцию через API
      const operationData = {
        engineer_name: engineer,
        operation_type: 'checkout',
        user_id: user.id
      };

      const response = await axios.post(`${API_BASE_URL}/operations/start`, operationData);
      console.log('Операция создана:', response.data);

      // Демо-инструменты
      const demoTools = [
        { id: 1, class_name: "Молоток", confidence: 95.5, manual_quantity: 1 },
        { id: 2, class_name: "Отвертка крестовая", confidence: 87.3, manual_quantity: 2 },
        { id: 3, class_name: "Гаечный ключ", confidence: 92.1, manual_quantity: 1 }
      ];

      setTools(demoTools);
      alert(`Операция начата для инженера: ${engineer}`);

    } catch (error) {
      console.error('Ошибка:', error);
      // Демо-режим при ошибке
      const demoTools = [
        { id: 1, class_name: "Молоток", confidence: 95.5, manual_quantity: 1 },
        { id: 2, class_name: "Отвертка", confidence: 87.3, manual_quantity: 1 },
        { id: 3, class_name: "Ключ гаечный", confidence: 92.1, manual_quantity: 1 }
      ];
      setTools(demoTools);
      alert('Демо-режим: операция начата');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="main-container">
      <header className="header">
        <h1>Учет инструментов</h1>
        <div className="user-info">
          <span>Оператор: {user.full_name}</span>
          {user.is_superuser && <span className="admin-badge">ADMIN</span>}
          <div className="backend-status">Режим: {backendStatus === 'available' ? '✅ Сервер' : '⚠️ Демо'}</div>
          <button onClick={onLogout}>Выйти</button>
        </div>
      </header>

      <div className="operation-panel">
        <div className="input-section">
          <div className="form-group">
            <label>ФИО инженера:</label>
            <input
              type="text"
              value={engineer}
              onChange={(e) => setEngineer(e.target.value)}
              placeholder="Введите ФИО инженера"
              disabled={loading}
            />
          </div>

          <button onClick={startOperation} disabled={!engineer.trim() || loading}>
            {loading ? 'Создание операции...' : 'Начать операцию'}
          </button>
        </div>

        {tools.length > 0 && (
          <div className="tools-section">
            <h3>Инструменты:</h3>
            <div className="tools-list">
              {tools.map((tool, index) => (
                <div key={index} className="tool-item">
                  <span>{tool.class_name}</span>
                  <span>Количество: {tool.manual_quantity}</span>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
