// App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE_URL = 'http://localhost:8000/api';

function App() {
  const [currentView, setCurrentView] = useState('login');
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));

  useEffect(() => {
    if (token) {
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º –≤–∞–ª–∏–¥–Ω–æ—Å—Ç—å —Ç–æ–∫–µ–Ω–∞
      verifyToken();
    }
  }, [token]);

  const verifyToken = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/auth/me`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUser(response.data);
      setCurrentView('main');
    } catch (error) {
      localStorage.removeItem('token');
      setToken(null);
    }
  };

  const handleLogin = (userData, authToken) => {
    setUser(userData);
    setToken(authToken);
    localStorage.setItem('token', authToken);
    setCurrentView('main');
  };

  const handleLogout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('token');
    setCurrentView('login');
  };

  return (
    <div className="App">
      {currentView === 'login' && (
        <LoginView onLogin={handleLogin} />
      )}
      {currentView === 'main' && user && (
        <MainView user={user} onLogout={handleLogout} />
      )}
    </div>
  );
}

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏

function LoginView({ onLogin }) {
  const [formData, setFormData] = useState({
    badge_id: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    // –î–µ–º–æ-—Ä–µ–∂–∏–º - –ø—Ä–æ–ø—É—Å–∫–∞–µ–º —Ä–µ–∞–ª—å–Ω—ã–π –∑–∞–ø—Ä–æ—Å –∫ API
    if (formData.badge_id === 'demo' || formData.badge_id === 'admin') {
      await new Promise(resolve => setTimeout(resolve, 1000)); // –ò–º–∏—Ç–∞—Ü–∏—è –∑–∞–¥–µ—Ä–∂–∫–∏

      const demoUser = {
        id: 1,
        badge_id: formData.badge_id,
        full_name: formData.badge_id === 'admin' ? '–ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä –°–∏—Å—Ç–µ–º—ã' : '–ò–≤–∞–Ω–æ–≤ –ò–≤–∞–Ω –ò–≤–∞–Ω–æ–≤–∏—á',
        is_active: true,
        is_superuser: formData.badge_id === 'admin'
      };

      onLogin(demoUser, 'demo-jwt-token');
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post(`${API_BASE_URL}/auth/login`, formData);
      onLogin(response.data.user, response.data.access_token);
    } catch (error) {
      setError('–û—à–∏–±–∫–∞ –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ badge_id –∏ –ø–∞—Ä–æ–ª—å.');

      // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏–π –¥–µ–º–æ-–≤—Ö–æ–¥ –ø—Ä–∏ –æ—à–∏–±–∫–µ
      await new Promise(resolve => setTimeout(resolve, 1000));
      const demoUser = {
        id: 1,
        badge_id: 'demo',
        full_name: '–î–µ–º–æ –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å',
        is_active: true,
        is_superuser: false
      };
      onLogin(demoUser, 'demo-jwt-token');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –±—ã—Å—Ç—Ä–æ–≥–æ –≤—Ö–æ–¥–∞
  const handleQuickLogin = (badgeId) => {
    setFormData({ badge_id: badgeId, password: 'password' });
    // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º —Ñ–æ—Ä–º—É —á–µ—Ä–µ–∑ –Ω–µ–±–æ–ª—å—à–æ–π —Ç–∞–π–º–∞—É—Ç
    setTimeout(() => {
      const fakeEvent = {
        preventDefault: () => {},
        target: document.querySelector('form')
      };
      handleSubmit(fakeEvent);
    }, 100);
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h2>–ê–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏—è –æ–ø–µ—Ä–∞—Ç–æ—Ä–∞</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Badge ID:</label>
            <input
              type="text"
              name="badge_id"
              value={formData.badge_id}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>–ü–∞—Ä–æ–ª—å:</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          {error && <div className="error-message">{error}</div>}
          <button type="submit" disabled={loading}>
            {loading ? '–í—Ö–æ–¥...' : '–í–æ–π—Ç–∏'}
          </button>
        </form>

        {/* –ö–Ω–æ–ø–∫–∏ –±—ã—Å—Ç—Ä–æ–≥–æ –≤—Ö–æ–¥–∞ - –í–ù–ï —Ñ–æ—Ä–º—ã */}
        <div className="quick-login-buttons">
          <p style={{textAlign: 'center', margin: '1rem 0', color: '#666'}}>–ò–ª–∏ –≤–æ–π–¥–∏—Ç–µ –±—ã—Å—Ç—Ä–æ:</p>
          <button 
            onClick={() => handleQuickLogin('demo')}
            style={{
              width: '100%',
              marginBottom: '0.5rem',
              background: '#28a745',
              border: 'none',
              padding: '0.75rem',
              borderRadius: '4px',
              color: 'white',
              cursor: 'pointer'
            }}
          >
            –ë—ã—Å—Ç—Ä—ã–π –≤—Ö–æ–¥ (Demo –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å)
          </button>

          <button 
            onClick={() => handleQuickLogin('admin')}
            style={{
              width: '100%',
              background: '#dc3545',
              border: 'none',
              padding: '0.75rem',
              borderRadius: '4px',
              color: 'white',
              cursor: 'pointer'
            }}
          >
            –í—Ö–æ–¥ –∫–∞–∫ –ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä
          </button>
        </div>

        <div style={{marginTop: '1rem', padding: '1rem', background: '#f8f9fa', borderRadius: '4px'}}>
          <p style={{margin: 0, fontSize: '0.9rem', color: '#666'}}>
            <strong>–î–µ–º–æ-—Ä–µ–∂–∏–º:</strong> –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ –∫–Ω–æ–ø–∫–∏ –±—ã—Å—Ç—Ä–æ–≥–æ –≤—Ö–æ–¥–∞ –∏–ª–∏ –≤–≤–µ–¥–∏—Ç–µ –ª—é–±—ã–µ –¥–∞–Ω–Ω—ã–µ –≤ —Ñ–æ—Ä–º—É
          </p>
        </div>
      </div>
    </div>
  );
}


