// App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE_URL = 'http://localhost:8000/api';

function App() {
  const [currentView, setCurrentView] = useState('login');
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));

  useEffect(() => {
    if (token) {
      // Проверяем валидность токена
      verifyToken();
    }
  }, [token]);

  const verifyToken = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/auth/me`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUser(response.data);
      setCurrentView('main');
    } catch (error) {
      localStorage.removeItem('token');
      setToken(null);
    }
  };

  const handleLogin = (userData, authToken) => {
    setUser(userData);
    setToken(authToken);
    localStorage.setItem('token', authToken);
    setCurrentView('main');
  };

  const handleLogout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('token');
    setCurrentView('login');
  };

  return (
    <div className="App">
      {currentView === 'login' && (
        <LoginView onLogin={handleLogin} />
      )}
      {currentView === 'main' && user && (
        <MainView user={user} onLogout={handleLogout} />
      )}
    </div>
  );
}

// Компонент авторизации

function LoginView({ onLogin }) {
  const [formData, setFormData] = useState({
    badge_id: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    if (e && e.preventDefault) {
      e.preventDefault();
    }

    setLoading(true);
    setError('');

    // Демо-режим - пропускаем реальный запрос к API
    if (formData.badge_id === 'demo' || formData.badge_id === 'admin') {
      await new Promise(resolve => setTimeout(resolve, 1000)); // Имитация задержки

      const demoUser = {
        id: 1,
        badge_id: formData.badge_id,
        full_name: formData.badge_id === 'admin' ? 'Администратор Системы' : 'Иванов Иван Иванович',
        is_active: true,
        is_superuser: formData.badge_id === 'admin'
      };

      onLogin(demoUser, 'demo-jwt-token');
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post(`${API_BASE_URL}/auth/login`, formData);
      onLogin(response.data.user, response.data.access_token);
    } catch (error) {
      setError('Ошибка авторизации. Проверьте badge_id и пароль.');

      // Автоматический демо-вход при ошибке
      await new Promise(resolve => setTimeout(resolve, 1000));
      const demoUser = {
        id: 1,
        badge_id: 'demo',
        full_name: 'Демо Пользователь',
        is_active: true,
        is_superuser: false
      };
      onLogin(demoUser, 'demo-jwt-token');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Функция для быстрого входа - УПРОЩЕННАЯ ВЕРСИЯ
  const handleQuickLogin = async (badgeId) => {
    setLoading(true);

    // Устанавливаем данные для демо-входа
    setFormData({ 
      badge_id: badgeId, 
      password: 'password' 
    });

    // Ждем немного чтобы состояние обновилось
    await new Promise(resolve => setTimeout(resolve, 100));

    // Создаем демо-пользователя
    const demoUser = {
      id: 1,
      badge_id: badgeId,
      full_name: badgeId === 'admin' ? 'Администратор Системы' : 'Иванов Иван Иванович',
      is_active: true,
      is_superuser: badgeId === 'admin'
    };

    // Имитируем задержку входа
    await new Promise(resolve => setTimeout(resolve, 1000));

    onLogin(demoUser, 'demo-jwt-token');
    setLoading(false);
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h2>Авторизация оператора</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Badge ID:</label>
            <input
              type="text"
              name="badge_id"
              value={formData.badge_id}
              onChange={handleChange}
              required
              disabled={loading}
            />
          </div>
          <div className="form-group">
            <label>Пароль:</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              disabled={loading}
            />
          </div>
          {error && <div className="error-message">{error}</div>}
          <button type="submit" disabled={loading}>
            {loading ? 'Вход...' : 'Войти'}
          </button>
        </form>

        {/* Кнопки быстрого входа - ВНЕ формы */}
        <div className="quick-login-buttons">
          <p style={{textAlign: 'center', margin: '1rem 0', color: '#666'}}>Или войдите быстро:</p>
          <button 
            onClick={() => handleQuickLogin('demo')}
            disabled={loading}
            style={{
              width: '100%',
              marginBottom: '0.5rem',
              background: '#28a745',
              border: 'none',
              padding: '0.75rem',
              borderRadius: '4px',
              color: 'white',
              cursor: 'pointer'
            }}
          >
            {loading ? 'Вход...' : 'Быстрый вход (Demo пользователь)'}
          </button>

          <button 
            onClick={() => handleQuickLogin('admin')}
            disabled={loading}
            style={{
              width: '100%',
              background: '#dc3545',
              border: 'none',
              padding: '0.75rem',
              borderRadius: '4px',
              color: 'white',
              cursor: 'pointer'
            }}
          >
            {loading ? 'Вход...' : 'Вход как Администратор'}
          </button>
        </div>

        <div style={{marginTop: '1rem', padding: '1rem', background: '#f8f9fa', borderRadius: '4px'}}>
          <p style={{margin: 0, fontSize: '0.9rem', color: '#666'}}>
            <strong>Демо-режим:</strong> Используйте кнопки быстрого входа или введите любые данные в форму
          </p>
        </div>
      </div>
    </div>
  );
}


